// src/app/interceptors/jwt.interceptor.ts
var jwtInterceptor = (req, next) => {
  console.log("\u{1F510} Interceptor executado para:", req.url);
  const rotasPublicas = [
    "/api/auth/login",
    "/api/auth/register",
    "/api/public/"
  ];
  const isRotaPublica = rotasPublicas.some((rota) => req.url.includes(rota));
  if (isRotaPublica) {
    console.log("\u26A0\uFE0F Rota p\xFAblica detectada, pulando interceptor");
    return next(req);
  }
  const token = localStorage.getItem("token");
  console.log("\u{1F4DD} Token encontrado:", token ? "SIM" : "N\xC3O");
  if (token) {
    console.log("\u2705 Adicionando token ao header Authorization");
    const cloned = req.clone({
      setHeaders: {
        Authorization: `Bearer ${token}`
      }
    });
    return next(cloned);
  } else {
    console.log("\u26A0\uFE0F Sem token, enviando requisi\xE7\xE3o sem autentica\xE7\xE3o");
    return next(req);
  }
};

